/// A certificate authority for use in tests.
pub struct Ca {
    dir: TempDir,
    name: X509Name,
    cert: X509,
    pkey: PKey<Private>,
}

impl Ca {
    fn make_ca(name: &str, parent: Option<&Ca>) -> Result<Ca, Box<dyn Error>> {
        let dir = tempfile::tempdir()?;
        let rsa = Rsa::generate(2048)?;
        let pkey = PKey::from_rsa(rsa)?;
        let name = {
            let mut builder = X509NameBuilder::new()?;
            builder.append_entry_by_nid(Nid::COMMONNAME, name)?;
            builder.build()
        };
        let cert = {
            let mut builder = X509::builder()?;
            builder.set_version(2)?;
            builder.set_pubkey(&pkey)?;
            builder.set_issuer_name(parent.map(|ca| &ca.name).unwrap_or(&name))?;
            builder.set_subject_name(&name)?;
            builder.set_not_before(&*Asn1Time::days_from_now(0)?)?;
            builder.set_not_after(&*Asn1Time::days_from_now(365)?)?;
            builder.append_extension(BasicConstraints::new().critical().ca().build()?)?;
            builder.sign(
                parent.map(|ca| &ca.pkey).unwrap_or(&pkey),
                MessageDigest::sha256(),
            )?;
            builder.build()
        };
        fs::write(dir.path().join("ca.crt"), cert.to_pem()?)?;
        Ok(Ca {
            dir,
            name,
            cert,
            pkey,
        })
    }

    /// Creates a new root certificate authority.
    pub fn new_root(name: &str) -> Result<Ca, Box<dyn Error>> {
        Ca::make_ca(name, None)
    }

    /// Returns the path to the CA's certificate.
    pub fn ca_cert_path(&self) -> PathBuf {
        self.dir.path().join("ca.crt")
    }

    /// Requests a new intermediate certificate authority.
    pub fn request_ca(&self, name: &str) -> Result<Ca, Box<dyn Error>> {
        Ca::make_ca(name, Some(self))
    }

    /// Generates a certificate with the specified Common Name (CN) that is
    /// signed by the CA.
    ///
    /// Returns the paths to the certificate and key.
    pub fn request_client_cert(&self, name: &str) -> Result<(PathBuf, PathBuf), Box<dyn Error>> {
        self.request_cert(name, iter::empty())
    }

    /// Like `request_client_cert`, but permits specifying additional IP
    /// addresses to attach as Subject Alternate Names.
    pub fn request_cert<I>(&self, name: &str, ips: I) -> Result<(PathBuf, PathBuf), Box<dyn Error>>
    where
        I: IntoIterator<Item = IpAddr>,
    {
        let rsa = Rsa::generate(2048)?;
        let pkey = PKey::from_rsa(rsa)?;
        let subject_name = {
            let mut builder = X509NameBuilder::new()?;
            builder.append_entry_by_nid(Nid::COMMONNAME, name)?;
            builder.build()
        };
        let cert = {
            let mut builder = X509::builder()?;
            builder.set_version(2)?;
            builder.set_pubkey(&pkey)?;
            builder.set_issuer_name(self.cert.subject_name())?;
            builder.set_subject_name(&subject_name)?;
            builder.set_not_before(&*Asn1Time::days_from_now(0)?)?;
            builder.set_not_after(&*Asn1Time::days_from_now(365)?)?;
            for ip in ips {
                builder.append_extension(
                    SubjectAlternativeName::new()
                        .ip(&ip.to_string())
                        .build(&builder.x509v3_context(None, None))?,
                )?;
            }
            builder.sign(&self.pkey, MessageDigest::sha256())?;
            builder.build()
        };
        let cert_path = self.dir.path().join(Path::new(name).with_extension("crt"));
        let key_path = self.dir.path().join(Path::new(name).with_extension("key"));
        fs::write(&cert_path, cert.to_pem()?)?;
        fs::write(&key_path, pkey.private_key_to_pem_pkcs8()?)?;
        Ok((cert_path, key_path))
    }
}

fn make_pg_tls<F>(configure: F) -> MakeTlsConnector
where
    F: Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack>,
{
    let mut connector_builder = SslConnector::builder(SslMethod::tls()).unwrap();
    // Disable TLS v1.3 because `postgres` and `hyper` produce stabler error
    // messages with TLS v1.2.
    //
    // Briefly, in TLS v1.3, failing to present a client certificate does not
    // error during the TLS handshake, as it does in TLS v1.2, but on the first
    // attempt to read from the stream. But both `postgres` and `hyper` write a
    // bunch of data before attempting to read from the stream. With a failed
    // TLS v1.3 connection, sometimes `postgres` and `hyper` succeed in writing
    // out this data, and then return a nice error message on the call to read.
    // But sometimes the connection is closed before they write out the data,
    // and so they report "connection closed" before they ever call read, never
    // noticing the underlying SSL error.
    //
    // It's unclear who's bug this is. Is it on `hyper`/`postgres` to call read
    // if writing to the stream fails to see if a TLS error occured? Is it on
    // OpenSSL to provide a better API [1]? Is it a protocol issue that ought to
    // be corrected in TLS v1.4? We don't want to answer these questions, so we
    // just avoid TLS v1.3 for now.
    //
    // [1]: https://github.com/openssl/openssl/issues/11118
    let options = connector_builder.options() | SslOptions::NO_TLSV1_3;
    connector_builder.set_options(options);
    configure(&mut connector_builder).unwrap();
    MakeTlsConnector::new(connector_builder.build())
}

fn make_http_tls<F>(configure: F) -> HttpsConnector<HttpConnector>
where
    F: Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack>,
{
    let mut connector_builder = SslConnector::builder(SslMethod::tls()).unwrap();
    // See comment in `make_pg_tls` about disabling TLS v1.3.
    let options = connector_builder.options() | SslOptions::NO_TLSV1_3;
    connector_builder.set_options(options);
    configure(&mut connector_builder).unwrap();
    let mut http = HttpConnector::new();
    http.enforce_http(false);
    HttpsConnector::with_connector(http, connector_builder).unwrap()
}

fn make_ws_tls<F>(uri: &Uri, configure: F) -> impl Read + Write
where
    F: Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack>,
{
    let mut connector_builder = SslConnector::builder(SslMethod::tls()).unwrap();
    // See comment in `make_pg_tls` about disabling TLS v1.3.
    let options = connector_builder.options() | SslOptions::NO_TLSV1_3;
    connector_builder.set_options(options);
    configure(&mut connector_builder).unwrap();
    let connector = connector_builder.build();

    let stream =
        TcpStream::connect(format!("{}:{}", uri.host().unwrap(), uri.port().unwrap())).unwrap();
    connector.connect(uri.host().unwrap(), stream).unwrap()
}

// Use two error types because some tests need to retry certain errors because
// there's a race condition for which is produced and they always need a
// postgres-style error.
enum Assert<E, D = ()> {
    Success,
    SuccessSuperuserCheck(bool),
    Err(E),
    DbErr(D),
}

enum TestCase<'a> {
    Pgwire {
        user: &'static str,
        password: Option<&'a str>,
        ssl_mode: SslMode,
        configure: Box<dyn Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack> + 'a>,
        assert: Assert<
            // A non-retrying, raw error.
            Box<dyn Fn(&tokio_postgres::error::Error) + 'a>,
            // A check that retries until it gets a DbError.
            Box<dyn Fn(&tokio_postgres::error::DbError) + 'a>,
        >,
    },
    Http {
        user: &'static str,
        scheme: Scheme,
        headers: &'a HeaderMap,
        configure: Box<dyn Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack> + 'a>,
        assert: Assert<Box<dyn Fn(Option<StatusCode>, String) + 'a>>,
    },
    Ws {
        auth: &'a WebSocketAuth,
        configure: Box<dyn Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack> + 'a>,
        assert: Assert<Box<dyn Fn(CloseCode, String) + 'a>>,
    },
}

fn assert_http_rejected() -> Assert<Box<dyn Fn(Option<StatusCode>, String)>> {
    Assert::Err(Box::new(|code, message| {
        const ALLOWED_MESSAGES: [&str; 2] = [
            "Connection reset by peer",
            "connection closed before message completed",
        ];
        assert_eq!(code, None);
        if !ALLOWED_MESSAGES
            .iter()
            .any(|allowed| message.contains(allowed))
        {
            panic!("TLS rejected with unexpected error message: {}", message)
        }
    }))
}

fn run_tests<'a>(header: &str, server: &util::Server, tests: &[TestCase<'a>]) {
    println!("==> {}", header);
    let runtime = Runtime::new().unwrap();
    for test in tests {
        match test {
            TestCase::Pgwire {
                user,
                password,
                ssl_mode,
                configure,
                assert,
            } => {
                println!(
                    "pgwire user={} password={:?} ssl_mode={:?}",
                    user, password, ssl_mode
                );

                let tls = make_pg_tls(configure);
                let mut pg_client = server.pg_config();
                pg_client
                    .ssl_mode(*ssl_mode)
                    .user(user)
                    .password(password.unwrap_or(""));

                match assert {
                    Assert::Success => {
                        let mut pg_client = pg_client.connect(tls).unwrap();
                        let row = pg_client.query_one("SELECT current_user", &[]).unwrap();
                        assert_eq!(row.get::<_, String>(0), *user);
                    }
                    Assert::SuccessSuperuserCheck(is_superuser) => {
                        let mut pg_client = pg_client.connect(tls.clone()).unwrap();
                        let row = pg_client.query_one("SELECT current_user", &[]).unwrap();
                        assert_eq!(row.get::<_, String>(0), *user);
                        let row = pg_client.query_one("SHOW is_superuser", &[]).unwrap();
                        let expected = if *is_superuser { "on" } else { "off" };
                        assert_eq!(row.get::<_, String>(0), *expected);
                    }
                    Assert::DbErr(check) => {
                        // This sometimes returns a network error, so retry until we get a db error.
                        Retry::default()
                            .max_duration(Duration::from_secs(10))
                            .retry(|_| {
                                // Due to delayed startup, we must attempt a query to
                                // check if there was a login error.
                                let err = match pg_client.connect(tls.clone()) {
                                    Ok(mut pg_client) => {
                                        pg_client.query_one("SELECT 1", &[]).unwrap_err()
                                    }
                                    Err(err) => err,
                                };
                                let Some(err) = err.as_db_error() else {
                                    return Err(());
                                };
                                check(err);
                                Ok(())
                            })
                            .unwrap();
                    }
                    Assert::Err(check) => {
                        // Due to delayed startup, we must attempt a query to
                        // check if there was a login error.
                        let err = match pg_client.connect(tls.clone()) {
                            Ok(mut pg_client) => pg_client.query_one("SELECT 1", &[]).unwrap_err(),
                            Err(err) => err,
                        };
                        check(&err);
                    }
                }
            }
            TestCase::Http {
                user,
                scheme,
                headers,
                configure,
                assert,
            } => {
                fn query_http_api<'a>(
                    query: &str,
                    uri: &Uri,
                    headers: &'a HeaderMap,
                    configure: &Box<
                        dyn Fn(&mut SslConnectorBuilder) -> Result<(), ErrorStack> + 'a,
                    >,
                    runtime: &Runtime,
                ) -> hyper::Result<Response<Body>> {
                    runtime.block_on(
                        hyper::Client::builder()
                            .build::<_, Body>(make_http_tls(configure))
                            .request({
                                let mut req = Request::post(uri);
                                for (k, v) in headers.iter() {
                                    req.headers_mut().unwrap().insert(k, v.clone());
                                }
                                req.headers_mut().unwrap().insert(
                                    "Content-Type",
                                    HeaderValue::from_static("application/json"),
                                );
                                req.body(Body::from(json!({ "query": query }).to_string()))
                                    .unwrap()
                            }),
                    )
                }

                fn assert_success_response(
                    res: hyper::Result<Response<Body>>,
                    expected_rows: Vec<Vec<String>>,
                    runtime: &Runtime,
                ) {
                    #[derive(Deserialize)]
                    struct Result {
                        rows: Vec<Vec<String>>,
                    }
                    #[derive(Deserialize)]
                    struct Response {
                        results: Vec<Result>,
                    }
                    let body = runtime
                        .block_on(body::to_bytes(res.unwrap().into_body()))
                        .unwrap();
                    let res: Response = serde_json::from_slice(&body).unwrap();
                    assert_eq!(res.results[0].rows, expected_rows)
                }

                println!("http user={} scheme={}", user, scheme);

                let uri = Uri::builder()
                    .scheme(scheme.clone())
                    .authority(&*format!(
                        "{}:{}",
                        Ipv4Addr::LOCALHOST,
                        server.inner.http_local_addr().port()
                    ))
                    .path_and_query("/api/sql")
                    .build()
                    .unwrap();
                let res = query_http_api(
                    "SELECT pg_catalog.current_user()",
                    &uri,
                    headers,
                    configure,
                    &runtime,
                );

                match assert {
                    Assert::Success => {
                        assert_success_response(res, vec![vec![user.to_string()]], &runtime);
                    }
                    Assert::SuccessSuperuserCheck(is_superuser) => {
                        assert_success_response(res, vec![vec![user.to_string()]], &runtime);
                        let res =
                            query_http_api("SHOW is_superuser", &uri, headers, configure, &runtime);
                        let expected = if *is_superuser { "on" } else { "off" };
                        assert_success_response(res, vec![vec![expected.to_string()]], &runtime);
                    }
                    Assert::Err(check) => {
                        let (code, message) = match res {
                            Ok(mut res) => {
                                let body = String::from_utf8_lossy(
                                    &runtime.block_on(body::to_bytes(res.body_mut())).unwrap(),
                                )
                                .into_owned();
                                (Some(res.status()), body)
                            }
                            Err(e) => (None, e.to_string()),
                        };
                        check(code, message)
                    }
                    Assert::DbErr(_) => unreachable!(),
                }
            }
            TestCase::Ws {
                auth,
                configure,
                assert,
            } => {
                println!("ws auth={:?}", auth);

                let uri = Uri::builder()
                    .scheme("wss")
                    .authority(&*format!(
                        "{}:{}",
                        Ipv4Addr::LOCALHOST,
                        server.inner.http_local_addr().port()
                    ))
                    .path_and_query("/api/experimental/sql")
                    .build()
                    .unwrap();
                let stream = make_ws_tls(&uri, configure);
                let (mut ws, _resp) = tungstenite::client(uri, stream).unwrap();
                //  let (mut ws, _resp) = tungstenite::connect(uri).unwrap();
                ws.write_message(Message::Text(serde_json::to_string(&auth).unwrap()))
                    .unwrap();

                ws.write_message(Message::Text(
                    r#"{"query": "SELECT pg_catalog.current_user()"}"#.into(),
                ))
                .unwrap();

                // Only supports reading a single row.
                fn assert_success_response(
                    ws: &mut tungstenite::WebSocket<impl Read + Write>,
                    mut expected_row_opt: Option<Vec<&str>>,
                    mut expected_tag_opt: Option<&str>,
                ) {
                    while expected_tag_opt.is_some() || expected_row_opt.is_some() {
                        let resp = ws.read_message().unwrap();
                        if let Message::Text(msg) = resp {
                            let msg: WebSocketResponse = serde_json::from_str(&msg).unwrap();
                            match (msg, &expected_row_opt, expected_tag_opt) {
                                (WebSocketResponse::Row(actual_row), Some(expected_row), _) => {
                                    assert_eq!(actual_row.len(), expected_row.len());
                                    for (actual_col, expected_col) in
                                        actual_row.into_iter().zip(expected_row.iter())
                                    {
                                        assert_eq!(&actual_col.to_string(), expected_col);
                                    }
                                    expected_row_opt = None;
                                }
                                (
                                    WebSocketResponse::CommandComplete(actual_tag),
                                    _,
                                    Some(expected_tag),
                                ) => {
                                    assert_eq!(actual_tag, expected_tag);
                                    expected_tag_opt = None;
                                }
                                (_, _, _) => {}
                            }
                        } else {
                            panic!("unexpected: {resp}");
                        }
                    }
                }

                match assert {
                    Assert::Success => assert_success_response(&mut ws, None, Some("SELECT 1")),
                    Assert::SuccessSuperuserCheck(is_superuser) => {
                        assert_success_response(&mut ws, None, Some("SELECT 1"));
                        ws.write_message(Message::Text(r#"{"query": "SHOW is_superuser"}"#.into()))
                            .unwrap();
                        let expected = if *is_superuser { "\"on\"" } else { "\"off\"" };
                        assert_success_response(&mut ws, Some(vec![expected]), Some("SELECT 1"));
                    }
                    Assert::Err(check) => {
                        let resp = ws.read_message().unwrap();
                        let (code, message) = match resp {
                            Message::Close(frame) => {
                                let frame = frame.unwrap();
                                (frame.code, frame.reason)
                            }
                            _ => panic!("unexpected: {resp}"),
                        };
                        check(code, message.to_string())
                    }
                    Assert::DbErr(_) => unreachable!(),
                }
            }
        }
    }
}

// Users is a mapping from (client, secret) -> email address.
fn start_mzcloud(
    encoding_key: EncodingKey,
    tenant_id: Uuid,
    users: BTreeMap<(String, String), String>,
    roles: BTreeMap<String, Vec<String>>,
    role_updates_rx: UnboundedReceiver<(String, Vec<String>)>,
    now: NowFn,
    expires_in_secs: i64,
) -> Result<MzCloudServer, anyhow::Error> {
    let refreshes = Arc::new(Mutex::new(0u64));
    let enable_refresh = Arc::new(AtomicBool::new(true));
    #[derive(Clone)]
    struct Context {
        encoding_key: EncodingKey,
        tenant_id: Uuid,
        users: BTreeMap<(String, String), String>,
        roles: BTreeMap<String, Vec<String>>,
        role_updates_rx: Arc<Mutex<tokio::sync::mpsc::UnboundedReceiver<(String, Vec<String>)>>>,
        now: NowFn,
        expires_in_secs: i64,
        // Uuid -> email
        refresh_tokens: Arc<Mutex<BTreeMap<String, String>>>,
        refreshes: Arc<Mutex<u64>>,
        enable_refresh: Arc<AtomicBool>,
    }
    let context = Context {
        encoding_key,
        tenant_id,
        users,
        roles,
        role_updates_rx: Arc::new(Mutex::new(role_updates_rx)),
        now,
        expires_in_secs,
        refresh_tokens: Arc::new(Mutex::new(BTreeMap::new())),
        refreshes: Arc::clone(&refreshes),
        enable_refresh: Arc::clone(&enable_refresh),
    };
    async fn handle(context: Context, req: Request<Body>) -> Result<Response<Body>, Infallible> {
        let (parts, body) = req.into_parts();
        let body = body::to_bytes(body).await.unwrap();
        let email: String = if parts.uri.path().ends_with(REFRESH_SUFFIX) {
            // Always count refresh attempts, even if enable_refresh is false.
            *context.refreshes.lock().unwrap() += 1;
            let args: RefreshToken = serde_json::from_slice(&body).unwrap();
            match (
                context
                    .refresh_tokens
                    .lock()
                    .unwrap()
                    .get(&args.refresh_token),
                context.enable_refresh.load(Ordering::Relaxed),
            ) {
                (Some(email), true) => email.to_string(),
                _ => {
                    return Ok(Response::builder()
                        .status(400)
                        .body(Body::from("unknown refresh token"))
                        .unwrap())
                }
            }
        } else {
            let args: ApiTokenArgs = serde_json::from_slice(&body).unwrap();
            match context
                .users
                .get(&(args.client_id.to_string(), args.secret.to_string()))
            {
                Some(email) => email.to_string(),
                None => {
                    return Ok(Response::builder()
                        .status(400)
                        .body(Body::from("unknown user"))
                        .unwrap())
                }
            }
        };
        let roles = context.roles.get(&email).cloned().unwrap_or_default();
        let refresh_token = Uuid::new_v4().to_string();
        context
            .refresh_tokens
            .lock()
            .unwrap()
            .insert(refresh_token.clone(), email.clone());
        let access_token = jsonwebtoken::encode(
            &jsonwebtoken::Header::new(jsonwebtoken::Algorithm::RS256),
            &Claims {
                exp: context.now.as_secs() + context.expires_in_secs,
                email,
                sub: Uuid::new_v4(),
                user_id: None,
                tenant_id: context.tenant_id,
                roles,
                permissions: Vec::new(),
            },
            &context.encoding_key,
        )
        .unwrap();
        let resp = ApiTokenResponse {
            expires: "".to_string(),
            expires_in: 0,
            access_token,
            refresh_token,
        };
        Ok(Response::new(Body::from(
            serde_json::to_vec(&resp).unwrap(),
        )))
    }

    let runtime = Arc::new(Runtime::new()?);
    let _guard = runtime.enter();
    let service = make_service_fn(move |_conn| {
        let mut context = context.clone();
        let service = service_fn(move |req| {
            while let Ok((email, roles)) = context.role_updates_rx.lock().unwrap().try_recv() {
                context.roles.insert(email, roles);
            }
            handle(context.clone(), req)
        });
        async move { Ok::<_, Infallible>(service) }
    });
    let addr = SocketAddr::new(IpAddr::V4(Ipv4Addr::LOCALHOST), 0);
    let server = Server::bind(&addr).serve(service);
    let url = format!("http://{}/", server.local_addr());
    let _handle = runtime.spawn_named(|| "mzcloud-mock-server", server);
    Ok(MzCloudServer {
        url,
        refreshes,
        enable_refresh,
        _runtime: runtime,
    })
}

struct MzCloudServer {
    url: String,
    refreshes: Arc<Mutex<u64>>,
    enable_refresh: Arc<AtomicBool>,
    _runtime: Arc<Runtime>,
}

fn make_header<H: Header>(h: H) -> HeaderMap {
    let mut map = HeaderMap::new();
    map.typed_insert(h);
    map
}

fn wait_for_refresh(frontegg_server: &MzCloudServer, expires_in_secs: u64) {
    let expected = *frontegg_server.refreshes.lock().unwrap() + 1;
    Retry::default()
        .factor(1.0)
        .max_duration(Duration::from_secs(expires_in_secs + 10))
        .retry(|_| {
            let refreshes = *frontegg_server.refreshes.lock().unwrap();
            if refreshes >= expected {
                Ok(())
            } else {
                Err(format!(
                    "expected refresh count {}, got {}",
                    expected, refreshes
                ))
            }
        })
        .unwrap();
}

#[test]
fn test_auth_expiry() {
    // This function verifies that the background expiry refresh task runs. This
    // is done by starting a web server that awaits the refresh request, which the
    // test waits for.

    let ca = Ca::new_root("test ca").unwrap();
    let (server_cert, server_key) = ca
        .request_cert("server", vec![IpAddr::V4(Ipv4Addr::LOCALHOST)])
        .unwrap();

    let tenant_id = Uuid::new_v4();
    let client_id = Uuid::new_v4();
    let secret = Uuid::new_v4();
    let users = BTreeMap::from([(
        (client_id.to_string(), secret.to_string()),
        "user@_.com".to_string(),
    )]);
    let roles = BTreeMap::from([("user@_.com".to_string(), Vec::new())]);
    let encoding_key =
        EncodingKey::from_rsa_pem(&ca.pkey.private_key_to_pem_pkcs8().unwrap()).unwrap();

    const EXPIRES_IN_SECS: u64 = 20;
    const REFRESH_BEFORE_SECS: u64 = 10;
    let (_role_tx, role_rx) = tokio::sync::mpsc::unbounded_channel();
    let frontegg_server = start_mzcloud(
        encoding_key,
        tenant_id,
        users,
        roles,
        role_rx,
        SYSTEM_TIME.clone(),
        i64::try_from(EXPIRES_IN_SECS).unwrap(),
    )
    .unwrap();
    let frontegg_auth = FronteggAuthentication::new(FronteggConfig {
        admin_api_token_url: frontegg_server.url.clone(),
        decoding_key: DecodingKey::from_rsa_pem(&ca.pkey.public_key_to_pem().unwrap()).unwrap(),
        tenant_id,
        now: SYSTEM_TIME.clone(),
        refresh_before_secs: i64::try_from(REFRESH_BEFORE_SECS).unwrap(),
        password_prefix: "mzauth_".to_string(),
        admin_role: "mzadmin".to_string(),
    });
    let frontegg_user = "user@_.com";
    let frontegg_password = &format!("mzauth_{client_id}{secret}");

    let config = util::Config::default()
        .with_tls(TlsMode::Require, server_cert, server_key)
        .with_frontegg(&frontegg_auth);
    let server = util::start_server(config).unwrap();

    let mut pg_client = server
        .pg_config()
        .ssl_mode(SslMode::Require)
        .user(frontegg_user)
        .password(frontegg_password)
        .connect(make_pg_tls(Box::new(|b: &mut SslConnectorBuilder| {
            Ok(b.set_verify(SslVerifyMode::NONE))
        })))
        .unwrap();

    assert_eq!(
        pg_client
            .query_one("SELECT current_user", &[])
            .unwrap()
            .get::<_, String>(0),
        frontegg_user
    );

    // Wait for a couple refreshes to happen.
    wait_for_refresh(&frontegg_server, EXPIRES_IN_SECS);
    wait_for_refresh(&frontegg_server, EXPIRES_IN_SECS);
    assert_eq!(
        pg_client
            .query_one("SELECT current_user", &[])
            .unwrap()
            .get::<_, String>(0),
        frontegg_user
    );

    // Disable giving out more refresh tokens.
    frontegg_server
        .enable_refresh
        .store(false, Ordering::Relaxed);
    wait_for_refresh(&frontegg_server, EXPIRES_IN_SECS);
    // Sleep until the expiry future should resolve.
    std::thread::sleep(Duration::from_secs(EXPIRES_IN_SECS + 1));
    assert!(pg_client.query_one("SELECT current_user", &[]).is_err());
}

#[allow(clippy::unit_arg)]
#[test]
fn test_auth_base() {
    mz_ore::test::init_logging();

    let ca = Ca::new_root("test ca").unwrap();
    let (server_cert, server_key) = ca
        .request_cert("server", vec![IpAddr::V4(Ipv4Addr::LOCALHOST)])
        .unwrap();

    let tenant_id = Uuid::new_v4();
    let client_id = Uuid::new_v4();
    let secret = Uuid::new_v4();
    let system_client_id = Uuid::new_v4();
    let system_secret = Uuid::new_v4();
    let users = BTreeMap::from([
        (
            (client_id.to_string(), secret.to_string()),
            "user@_.com".to_string(),
        ),
        (
            (system_client_id.to_string(), system_secret.to_string()),
            SYSTEM_USER.name.to_string(),
        ),
    ]);
    let roles = BTreeMap::from([
        ("user@_.com".to_string(), Vec::new()),
        (SYSTEM_USER.name.to_string(), Vec::new()),
    ]);
    let encoding_key =
        EncodingKey::from_rsa_pem(&ca.pkey.private_key_to_pem_pkcs8().unwrap()).unwrap();
    let timestamp = Arc::new(Mutex::new(500_000));
    let now = {
        let timestamp = Arc::clone(&timestamp);
        NowFn::from(move || *timestamp.lock().unwrap())
    };
    let claims = Claims {
        exp: 1000,
        email: "user@_.com".to_string(),
        sub: Uuid::new_v4(),
        user_id: None,
        tenant_id,
        roles: Vec::new(),
        permissions: Vec::new(),
    };
    let frontegg_jwt = jsonwebtoken::encode(
        &jsonwebtoken::Header::new(jsonwebtoken::Algorithm::RS256),
        &claims,
        &encoding_key,
    )
    .unwrap();
    let bad_tenant_claims = {
        let mut claims = claims.clone();
        claims.tenant_id = Uuid::new_v4();
        claims
    };
    let bad_tenant_jwt = jsonwebtoken::encode(
        &jsonwebtoken::Header::new(jsonwebtoken::Algorithm::RS256),
        &bad_tenant_claims,
        &encoding_key,
    )
    .unwrap();
    let expired_claims = {
        let mut claims = claims;
        claims.exp = 0;
        claims
    };
    let expired_jwt = jsonwebtoken::encode(
        &jsonwebtoken::Header::new(jsonwebtoken::Algorithm::RS256),
        &expired_claims,
        &encoding_key,
    )
    .unwrap();
    let (_role_tx, role_rx) = tokio::sync::mpsc::unbounded_channel();
    let frontegg_server = start_mzcloud(
        encoding_key,
        tenant_id,
        users,
        roles,
        role_rx,
        now.clone(),
        1_000,
    )
    .unwrap();
    let frontegg_auth = FronteggAuthentication::new(FronteggConfig {
        admin_api_token_url: frontegg_server.url,
        decoding_key: DecodingKey::from_rsa_pem(&ca.pkey.public_key_to_pem().unwrap()).unwrap(),
        tenant_id,
        now,
        refresh_before_secs: 0,
        password_prefix: "mzauth_".to_string(),
        admin_role: "mzadmin".to_string(),
    });
    let frontegg_user = "user@_.com";
    let frontegg_password = &format!("mzauth_{client_id}{secret}");
    let frontegg_basic = Authorization::basic(frontegg_user, frontegg_password);
    let frontegg_header_basic = make_header(frontegg_basic);

    let frontegg_system_password = &format!("mzauth_{system_client_id}{system_secret}");
    let frontegg_system_basic = Authorization::basic(&SYSTEM_USER.name, frontegg_system_password);
    let frontegg_system_header_basic = make_header(frontegg_system_basic);

    let no_headers = HeaderMap::new();

    // Test connecting to a server that requires TLS and uses Materialize Cloud for
    // authentication.
    let config = util::Config::default()
        .with_tls(TlsMode::Require, &server_cert, &server_key)
        .with_frontegg(&frontegg_auth);
    let server = util::start_server(config).unwrap();
    run_tests(
        "TlsMode::Require, MzCloud",
        &server,
        &[
            TestCase::Ws {
                auth: &WebSocketAuth::Basic {
                    user: frontegg_user.to_string(),
                    password: frontegg_password.to_string(),
                },
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            TestCase::Ws {
                auth: &WebSocketAuth::Bearer {
                    token: frontegg_jwt.clone(),
                },
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            TestCase::Ws {
                auth: &WebSocketAuth::Basic {
                    user: "bad user".to_string(),
                    password: frontegg_password.to_string(),
                },
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, CloseCode::Protocol);
                    assert_eq!(message, "unauthorized");
                })),
            },
            // TLS with a password should succeed.
            TestCase::Pgwire {
                user: frontegg_user,
                password: Some(frontegg_password),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &frontegg_header_basic,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Password can be base64 encoded UUID bytes.
            TestCase::Pgwire {
                user: frontegg_user,
                password: {
                    let mut buf = vec![];
                    buf.extend(client_id.as_bytes());
                    buf.extend(secret.as_bytes());
                    Some(&format!(
                        "mzauth_{}",
                        base64::encode_config(buf, base64::URL_SAFE)
                    ))
                },
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Password can be base64 encoded UUID bytes without padding.
            TestCase::Pgwire {
                user: frontegg_user,
                password: {
                    let mut buf = vec![];
                    buf.extend(client_id.as_bytes());
                    buf.extend(secret.as_bytes());
                    Some(&format!(
                        "mzauth_{}",
                        base64::encode_config(buf, base64::URL_SAFE_NO_PAD)
                    ))
                },
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Password can include arbitrary special characters.
            TestCase::Pgwire {
                user: frontegg_user,
                password: {
                    let mut password = frontegg_password.clone();
                    password.insert(10, '-');
                    password.insert_str(15, "@#!");
                    Some(&password.clone())
                },
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Bearer auth doesn't need the clientid or secret.
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::bearer(&frontegg_jwt).unwrap()),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // No TLS fails.
            TestCase::Pgwire {
                user: frontegg_user,
                password: Some(frontegg_password),
                ssl_mode: SslMode::Disable,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(
                        *err.code(),
                        SqlState::SQLSERVER_REJECTED_ESTABLISHMENT_OF_SQLCONNECTION
                    );
                    assert_eq!(err.message(), "TLS encryption is required");
                })),
            },
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTP,
                headers: &frontegg_header_basic,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: assert_http_rejected(),
            },
            // Wrong, but existing, username.
            TestCase::Pgwire {
                user: "materialize",
                password: Some(frontegg_password),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(err.message(), "invalid password");
                    assert_eq!(*err.code(), SqlState::INVALID_PASSWORD);
                })),
            },
            TestCase::Http {
                user: "materialize",
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::basic("materialize", frontegg_password)),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // Wrong password.
            TestCase::Pgwire {
                user: frontegg_user,
                password: Some("bad password"),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(err.message(), "invalid password");
                    assert_eq!(*err.code(), SqlState::INVALID_PASSWORD);
                })),
            },
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::basic(frontegg_user, "bad password")),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // Bad password prefix.
            TestCase::Pgwire {
                user: frontegg_user,
                password: Some(&format!("mznope_{client_id}{secret}")),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(err.message(), "invalid password");
                    assert_eq!(*err.code(), SqlState::INVALID_PASSWORD);
                })),
            },
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::basic(
                    frontegg_user,
                    &format!("mznope_{client_id}{secret}"),
                )),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // No password.
            TestCase::Pgwire {
                user: frontegg_user,
                password: None,
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(err.message(), "invalid password");
                    assert_eq!(*err.code(), SqlState::INVALID_PASSWORD);
                })),
            },
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &no_headers,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // Bad auth scheme
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &HeaderMap::from_iter(vec![(
                    AUTHORIZATION,
                    HeaderValue::from_static("Digest username=materialize"),
                )]),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // Bad tenant.
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::bearer(&bad_tenant_jwt).unwrap()),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // Expired.
            TestCase::Http {
                user: frontegg_user,
                scheme: Scheme::HTTPS,
                headers: &make_header(Authorization::bearer(&expired_jwt).unwrap()),
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_eq!(message, "unauthorized");
                })),
            },
            // System user cannot login via external ports.
            TestCase::Pgwire {
                user: &*SYSTEM_USER.name,
                password: Some(frontegg_system_password),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|err| {
                    assert_contains!(err.to_string(), "unauthorized login to user 'mz_system'");
                })),
            },
            TestCase::Http {
                user: &*SYSTEM_USER.name,
                scheme: Scheme::HTTPS,
                headers: &frontegg_system_header_basic,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, Some(StatusCode::UNAUTHORIZED));
                    assert_contains!(message, "unauthorized");
                })),
            },
            TestCase::Ws {
                auth: &WebSocketAuth::Basic {
                    user: (&*SYSTEM_USER.name).into(),
                    password: frontegg_system_password.to_string(),
                },
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Err(Box::new(|code, message| {
                    assert_eq!(code, CloseCode::Protocol);
                    assert_eq!(message, "unauthorized");
                })),
            },
        ],
    );
    drop(server);

    // Test TLS modes with a server that does not support TLS.
    let server = util::start_server(util::Config::default()).unwrap();
    run_tests(
        "TlsMode::Disable",
        &server,
        &[
            // Explicitly disabling TLS should succeed.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Disable,
                configure: Box::new(|_| Ok(())),
                assert: Assert::Success,
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTP,
                headers: &no_headers,
                configure: Box::new(|_| Ok(())),
                assert: Assert::Success,
            },
            // Preferring TLS should fall back to no TLS.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Prefer,
                configure: Box::new(|_| Ok(())),
                assert: Assert::Success,
            },
            // Requiring TLS should fail.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Require,
                configure: Box::new(|_| Ok(())),
                assert: Assert::Err(Box::new(|err| {
                    assert_eq!(
                        err.to_string(),
                        "error performing TLS handshake: server does not support TLS",
                    )
                })),
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTPS,
                headers: &no_headers,
                configure: Box::new(|_| Ok(())),
                assert: Assert::Err(Box::new(|code, message| {
                    // Connecting to an HTTP server via HTTPS does not yield
                    // a graceful error message. This could plausibly change
                    // due to OpenSSL or Hyper refactorings.
                    assert!(code.is_none());
                    assert_contains!(message, "ssl3_get_record:wrong version number");
                })),
            },
            // System user cannot login via external ports.
            TestCase::Pgwire {
                user: &*SYSTEM_USER.name,
                password: None,
                ssl_mode: SslMode::Disable,
                configure: Box::new(|_| Ok(())),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_contains!(err.to_string(), "unauthorized login to user 'mz_system'");
                })),
            },
        ],
    );
    drop(server);

    // Test TLS modes with a server that requires TLS.
    let config = util::Config::default().with_tls(TlsMode::Require, &server_cert, &server_key);
    let server = util::start_server(config).unwrap();
    run_tests(
        "TlsMode::Require",
        &server,
        &[
            // Non-existent role will be created.
            TestCase::Pgwire {
                user: frontegg_user,
                password: Some(frontegg_password),
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Test that specifying an mzcloud header does nothing and uses the default
            // user.
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTPS,
                headers: &frontegg_header_basic,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Disabling TLS should fail.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Disable,
                configure: Box::new(|_| Ok(())),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_eq!(
                        *err.code(),
                        SqlState::SQLSERVER_REJECTED_ESTABLISHMENT_OF_SQLCONNECTION
                    );
                    assert_eq!(err.message(), "TLS encryption is required");
                })),
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTP,
                headers: &no_headers,
                configure: Box::new(|_| Ok(())),
                assert: assert_http_rejected(),
            },
            // Preferring TLS should succeed.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Prefer,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // Requiring TLS should succeed.
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTPS,
                headers: &no_headers,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::Success,
            },
            // System user cannot login via external ports.
            TestCase::Pgwire {
                user: &*SYSTEM_USER.name,
                password: None,
                ssl_mode: SslMode::Prefer,
                configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                assert: Assert::DbErr(Box::new(|err| {
                    assert_contains!(err.to_string(), "unauthorized login to user 'mz_system'");
                })),
            },
        ],
    );
    drop(server);
}

#[test]
fn test_auth_intermediate_ca() {
    // Create a CA, an intermediate CA, and a server key pair signed by the
    // intermediate CA.
    let ca = Ca::new_root("test ca").unwrap();
    let intermediate_ca = ca.request_ca("intermediary").unwrap();
    let (server_cert, server_key) = intermediate_ca
        .request_cert("server", vec![IpAddr::V4(Ipv4Addr::LOCALHOST)])
        .unwrap();

    // Create a certificate chain bundle that contains the server's certificate
    // and the intermediate CA's certificate.
    let server_cert_chain = {
        let path = intermediate_ca.dir.path().join("server.chain.crt");
        let mut buf = vec![];
        File::open(&server_cert)
            .unwrap()
            .read_to_end(&mut buf)
            .unwrap();
        File::open(intermediate_ca.ca_cert_path())
            .unwrap()
            .read_to_end(&mut buf)
            .unwrap();
        fs::write(&path, buf).unwrap();
        path
    };

    // When the server presents only its own certificate, without the
    // intermediary, the client should fail to verify the chain.
    let config = util::Config::default().with_tls(TlsMode::Require, &server_cert, &server_key);
    let server = util::start_server(config).unwrap();
    run_tests(
        "TlsMode::Require",
        &server,
        &[
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| b.set_ca_file(ca.ca_cert_path())),
                assert: Assert::Err(Box::new(|err| {
                    assert_contains!(err.to_string(), "unable to get local issuer certificate");
                })),
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTPS,
                headers: &HeaderMap::new(),
                configure: Box::new(|b| b.set_ca_file(ca.ca_cert_path())),
                assert: Assert::Err(Box::new(|code, message| {
                    assert!(code.is_none());
                    assert_contains!(message, "unable to get local issuer certificate");
                })),
            },
        ],
    );
    drop(server);

    // When the server is configured to present the entire certificate chain,
    // the client should be able to verify the chain even though it only knows
    // about the root CA.
    let config = util::Config::default().with_tls(TlsMode::Require, server_cert_chain, &server_key);
    let server = util::start_server(config).unwrap();
    run_tests(
        "TlsMode::Require",
        &server,
        &[
            TestCase::Pgwire {
                user: "materialize",
                password: None,
                ssl_mode: SslMode::Require,
                configure: Box::new(|b| b.set_ca_file(ca.ca_cert_path())),
                assert: Assert::Success,
            },
            TestCase::Http {
                user: &*HTTP_DEFAULT_USER.name,
                scheme: Scheme::HTTPS,
                headers: &HeaderMap::new(),
                configure: Box::new(|b| b.set_ca_file(ca.ca_cert_path())),
                assert: Assert::Success,
            },
        ],
    );
    drop(server);
}

#[test]
fn test_auth_admin() {
    mz_ore::test::init_logging();

    let ca = Ca::new_root("test ca").unwrap();
    let (server_cert, server_key) = ca
        .request_cert("server", vec![IpAddr::V4(Ipv4Addr::LOCALHOST)])
        .unwrap();

    let tenant_id = Uuid::new_v4();
    let client_id = Uuid::new_v4();
    let secret = Uuid::new_v4();
    let admin_client_id = Uuid::new_v4();
    let admin_secret = Uuid::new_v4();

    let frontegg_user = "user@_.com";
    let admin_frontegg_user = "admin@_.com";

    let admin_role = "mzadmin";

    let users = BTreeMap::from([
        (
            (client_id.to_string(), secret.to_string()),
            frontegg_user.to_string(),
        ),
        (
            (admin_client_id.to_string(), admin_secret.to_string()),
            admin_frontegg_user.to_string(),
        ),
    ]);
    let roles = BTreeMap::from([
        (frontegg_user.to_string(), Vec::new()),
        (
            admin_frontegg_user.to_string(),
            vec![admin_role.to_string()],
        ),
    ]);
    let encoding_key =
        EncodingKey::from_rsa_pem(&ca.pkey.private_key_to_pem_pkcs8().unwrap()).unwrap();
    let now = SYSTEM_TIME.clone();

    const EXPIRES_IN_SECS: u64 = 20;
    const REFRESH_BEFORE_SECS: u64 = 10;
    let (role_tx, role_rx) = tokio::sync::mpsc::unbounded_channel();
    let frontegg_server = start_mzcloud(
        encoding_key,
        tenant_id,
        users,
        roles,
        role_rx,
        now.clone(),
        i64::try_from(EXPIRES_IN_SECS).unwrap(),
    )
    .unwrap();
    let password_prefix = "mzauth_";
    let frontegg_auth = FronteggAuthentication::new(FronteggConfig {
        admin_api_token_url: frontegg_server.url.clone(),
        decoding_key: DecodingKey::from_rsa_pem(&ca.pkey.public_key_to_pem().unwrap()).unwrap(),
        tenant_id,
        now,
        refresh_before_secs: i64::try_from(REFRESH_BEFORE_SECS).unwrap(),
        password_prefix: password_prefix.to_string(),
        admin_role: admin_role.to_string(),
    });

    let frontegg_password = &format!("{password_prefix}{client_id}{secret}");
    let frontegg_basic = Authorization::basic(frontegg_user, frontegg_password);
    let frontegg_header_basic = make_header(frontegg_basic);

    let admin_frontegg_password = &format!("{password_prefix}{admin_client_id}{admin_secret}");
    let admin_frontegg_basic = Authorization::basic(admin_frontegg_user, admin_frontegg_password);
    let admin_frontegg_header_basic = make_header(admin_frontegg_basic);

    {
        let config = util::Config::default()
            .with_tls(TlsMode::Require, &server_cert, &server_key)
            .with_frontegg(&frontegg_auth);
        let server = util::start_server(config).unwrap();

        run_tests(
            "Non-superuser",
            &server,
            &[
                TestCase::Pgwire {
                    user: frontegg_user,
                    password: Some(frontegg_password),
                    ssl_mode: SslMode::Require,
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(false),
                },
                TestCase::Http {
                    user: frontegg_user,
                    scheme: Scheme::HTTPS,
                    headers: &frontegg_header_basic,
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(false),
                },
                TestCase::Ws {
                    auth: &WebSocketAuth::Basic {
                        user: frontegg_user.to_string(),
                        password: frontegg_password.to_string(),
                    },
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(false),
                },
            ],
        );
    }

    {
        let config = util::Config::default()
            .with_tls(TlsMode::Require, &server_cert, &server_key)
            .with_frontegg(&frontegg_auth);
        let server = util::start_server(config).unwrap();

        run_tests(
            "Superuser",
            &server,
            &[
                TestCase::Pgwire {
                    user: admin_frontegg_user,
                    password: Some(admin_frontegg_password),
                    ssl_mode: SslMode::Require,
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(true),
                },
                TestCase::Http {
                    user: admin_frontegg_user,
                    scheme: Scheme::HTTPS,
                    headers: &admin_frontegg_header_basic,
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(true),
                },
                TestCase::Ws {
                    auth: &WebSocketAuth::Basic {
                        user: admin_frontegg_user.to_string(),
                        password: admin_frontegg_password.to_string(),
                    },
                    configure: Box::new(|b| Ok(b.set_verify(SslVerifyMode::NONE))),
                    assert: Assert::SuccessSuperuserCheck(true),
                },
            ],
        );
    }

    {
        let config = util::Config::default()
            .with_tls(TlsMode::Require, &server_cert, &server_key)
            .with_frontegg(&frontegg_auth);
        let server = util::start_server(config).unwrap();

        let mut pg_client = server
            .pg_config()
            .ssl_mode(SslMode::Require)
            .user(frontegg_user)
            .password(frontegg_password)
            .connect(make_pg_tls(Box::new(|b: &mut SslConnectorBuilder| {
                Ok(b.set_verify(SslVerifyMode::NONE))
            })))
            .unwrap();

        assert_eq!(
            pg_client
                .query_one("SHOW is_superuser", &[])
                .unwrap()
                .get::<_, String>(0),
            "off"
        );

        role_tx
            .send((frontegg_user.to_string(), vec![admin_role.to_string()]))
            .unwrap();
        wait_for_refresh(&frontegg_server, EXPIRES_IN_SECS);

        assert_eq!(
            pg_client
                .query_one("SHOW is_superuser", &[])
                .unwrap()
                .get::<_, String>(0),
            "on"
        );

        role_tx
            .send((frontegg_user.to_string(), Vec::new()))
            .unwrap();
        wait_for_refresh(&frontegg_server, EXPIRES_IN_SECS);

        assert_eq!(
            pg_client
                .query_one("SHOW is_superuser", &[])
                .unwrap()
                .get::<_, String>(0),
            "off"
        );
    }
}
